deg = float(input("Enter x(deg) :"))
rad = (deg*3.)/180

def Factorial(i):
    b = 1   
    for j in range(1,(i+1)):
        b = b * j
    return b

def Sin(rad):
    vals = 0
    for i  in range(1,100,2):
        vals = (vals + (((-1)**((i+3)/2))*(rad**i))/(Factorial(i)))
    return vals

def Cos(rad):
    valc = 0
    for i  in range(0,100,2):
        valc = (valc + (((-1)**((i+4)/2))*(rad**i))/(Factorial(i)))
    return valc

def Tan(rad):
    return Sin(rad)/Cos(rad)
